CREATE TABLE `gift_certificate` (`id_gift_certificate` bigint NOT NULL AUTO_INCREMENT, `name` varchar(300) DEFAULT NULL, `description` varchar(500) DEFAULT NULL, `price` decimal(10,2) DEFAULT NULL, `duration` int DEFAULT NULL, `create_date` datetime NOT NULL, `last_update_date` datetime DEFAULT NULL, PRIMARY KEY (`id_gift_certificate`));
CREATE TABLE `tag` (`id_tag` bigint NOT NULL AUTO_INCREMENT, `name` varchar(100) DEFAULT NULL, PRIMARY KEY (`id_tag`));
CREATE TABLE `certificate_tag` (`id_gift_certificate` bigint NOT NULL, `id_tag` bigint NOT NULL, PRIMARY KEY (`id_gift_certificate`,`id_tag`), CONSTRAINT `fk_gift_certificate` FOREIGN KEY (`id_gift_certificate`) REFERENCES `gift_certificate` (`id_gift_certificate`), CONSTRAINT `fk_tag` FOREIGN KEY (`id_tag`) REFERENCES `tag` (`id_tag`));